import React, { Component } from 'react'
import { View, StyleSheet } from 'react-native';
import { connect } from 'react-redux';
import Deck from "../Deck";
import { removeDeck } from '../../actions';
import { white,orange,lightblue, purple, gray, lightPurp, red, azure } from '../../utils/colors';
import CustomClickButton from "../component/CustomClickButton";

class DeckDetails extends Component {

    handleDelete = id => {
        const { removeDeck, navigation } = this.props;
        removeDeck(id);
        navigation.navigate("Decks");
    };

    render() {

        const { deck } = this.props;

        return (
            <View style={styles.container}>
              <Deck id={deck && deck.title} />
              <View>
                <CustomClickButton
                  btnStyle={{ backgroundColor: orange, borderColor: gray }}
                  txtStyle={{ color: white }}
                  onPress={() =>
                    this.props.navigation.navigate('NewCard', { title: deck.title })
                  }
                >
                  Add Card
                </CustomClickButton>
                <CustomClickButton
                  btnStyle={{ backgroundColor: azure, borderColor: gray }}
                  txtStyle={{ color: white }}
                  onPress={() =>
                    this.props.navigation.navigate('Quiz', { title: deck.title })
                  }
                >
                  Start Quiz
                </CustomClickButton>
                <CustomClickButton
                  btnStyle={{ backgroundColor: red, borderColor: gray }}
                  txtStyle={{ color: white }}
                  onPress={() => this.handleDelete(deck.title)}
                >
                  Delete Deck
              </CustomClickButton>
              </View>
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      paddingLeft: 16,
      paddingRight: 16,
      justifyContent: 'space-around',
      paddingTop: 16,
      paddingBottom: 16,
      backgroundColor: azure
    }
});

const mapStateToProps = (state, { navigation }) => {
    const title = navigation.getParam('title', 'undefined');
    const deck = state[title];

    return {
      deck
    };
};

const mapDispatchToProps = (dispatch) => (
    {
      removeDeck: (title) => dispatch(removeDeck(title)),
    }
)

export default connect(mapStateToProps,mapDispatchToProps)(DeckDetails);
